/*
 * functions for testing if the two specified values are equal:
 */

bool _equalsBool(const void *a, const void *b) {
    Boolean *x = new_Boolean(*(bool*)a);
    Boolean *y = new_Boolean(*(bool*)b);
    bool equal = equalsBool(x, y);
    free(x);
    free(y);

    return equal;
}

