(**
 * Advances this scanner past the current line and returns the input that was skipped.
 *
 * @return the line that was skipped
 *)
function Scanner.nextLine() : String_;
var
  str: String_;
begin
  if length(line) = 0 then begin
     exit(readLine());
  end;

  str := String_.create(line);
  line := '';
  nextLine := str;
end;

